","keccak256":"0xe2e337e6dde9ef6b680e07338c493ebea1b5fd09b43424112868e9cc1706bca7","license":"MIT"},"contracts/Treasury.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";

import "./types/AccessControlled.sol";

import "hardhat/console.sol";

contract Treasury is Context, AccessControlled {

    /* ========== EVENTS ========== */

    event DepositERC20(address indexed token, uint256 amount);

    event WithdrawERC20(address indexed token, uint256 amount);


    /* ========== DATA STRUCTURES ========== */

    enum STATUS {
        NON,
        RESERVEDEPOSITOR,
        RESERVESPENDER
    }

    enum TokenType   { 
        NON, 
        ERC20, 
        ERC1155, 
        ERC721, 
        NATIVE 
    }


    /* ========== STATE VARIABLES ========== */

    string internal notApproved = "Treasury: not approved";
    string internal invalidToken = "Treasury: invalid token";

    mapping(STATUS => mapping(address => bool)) public permissions;

    // Fee - default 0.1%
    uint256 public fee = 10; // 1 == 0.01%
    uint256 public feeDivisor = 10000;

    // Treasury balance
    mapping(address => uint256) public erc20Treasury; // tokenAddress => feePot
    mapping(address => mapping(uint256 => uint256)) public erc1155Treasury; // tokenAddress => tokenId => feePot

    /* ========== CONSTRUCTOR ========== */

    constructor(
        address _authority
    ) AccessControlled(IAuthority(_authority)) {
        // TBD
    }

    /**
     * @notice allow address to deposit an asset 
     * @param _amount uint256
     * @param _token address
     * @param _sender address
     */
    function deposit(
        uint256 _amount,
        address _token,        
        address _sender // remove and start using tx.origin
    ) external {        
        IERC20(_token).transferFrom(_sender, address(this), _amount);
        emit DepositERC20(_token, _amount);
    
        // collect fees
        // collect fee from executor's deposit? 
        collectFee(_token, _amount);
    }

    /**
     * @notice allow approved address to withdraw from reserves ||||| TODO: wait for payment approval
     * @param _amount uint256
     * @param _token address
     */
    function withdraw(uint256 _amount, address _token) external {
        require(permissions[STATUS.RESERVESPENDER][msg.sender], notApproved);
      
        IERC20(_token).transferFrom(address(this), msg.sender, _amount);
        emit WithdrawERC20(_token, _amount);
    }
    
     function setPermission(
        STATUS _status,
        address _address,
        bool _permission
    ) public onlyGovernor {
        permissions[_status][_address] = _permission;
    }

    function getTokenBalance(address _token) public view returns (uint256) {
        return IERC20(_token).balanceOf(address(this));
    }

    function collectFee(address _token, uint256 _amout) internal {     
        uint256 _fee = (_amout * fee) / feeDivisor;
        erc20Treasury[_token] += _fee;
    }

    function setFee(uint256 _fee) public onlyGovernor {
        require(_fee < 10000); // _fee must be less than 100%
        fee = _fee;
    }

    function getTreasuryBalance(address _token) public view returns (uint256) {
        return erc20Treasury[_token];
    }
}","keccak256":"0xe3705e571f4b376230311413ee488048bf509b7e818e951d3615220f41138211","license":"MIT"},"contracts/interfaces/IAuthority.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity >=0.8.9;

interface IAuthority {
    /* ========== EVENTS ========== */

    event GovernorPushed(address indexed from, address indexed to, bool _effectiveImmediately);
    event GuardianPushed(address indexed from, address indexed to, bool _effectiveImmediately);
    event PolicyPushed(address indexed from, address indexed to, bool _effectiveImmediately);
    event VaultPushed(address indexed from, address indexed to, bool _effectiveImmediately);

    event GovernorPulled(address indexed from, address indexed to);
    event GuardianPulled(address indexed from, address indexed to);
    event PolicyPulled(address indexed from, address indexed to);
    event VaultPulled(address indexed from, address indexed to);

    /* ========== VIEW ========== */

    function governor() external view returns (address);

    function guardian() external view returns (address);

    function policy() external view returns (address);

    function vault() external view returns (address);
}
","keccak256":"0x162ef81a6d172910d8817409df50dc7155f746bcd4173a9e6c844a24abd78fca","license":"AGPL-3.0"},"contracts/types/AccessControlled.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "../interfaces/IAuthority.sol";

error UNAUTHORIZED();
error AUTHORITY_INITIALIZED();

/// @dev Reasoning for this contract = modifiers literaly copy code
/// instead of pointing towards the logic to execute. Over many
/// functions this bloats contract size unnecessarily.
/// imho modifiers are a meme.
abstract contract AccessControlled {
    /* ========== EVENTS ========== */

    event AuthorityUpdated(IAuthority authority);

    /* ========== STATE VARIABLES ========== */

    IAuthority public authority;

    /* ========== Constructor ========== */

    constructor(IAuthority _authority) {
        authority = _authority;
        emit AuthorityUpdated(_authority);
    }

    /* ========== "MODIFIERS" ========== */

    modifier onlyGovernor {
	_onlyGovernor();
	_;
    }

    modifier onlyGuardian {
	_onlyGuardian();
	_;
    }

    modifier onlyPolicy {
	_onlyPolicy();
	_;
    }

    modifier onlyVault {
	_onlyVault();
	_;
    }

    /* ========== GOV ONLY ========== */

    function initializeAuthority(IAuthority _newAuthority) internal {
        if (authority != IAuthority(address(0))) revert AUTHORITY_INITIALIZED();
        authority = _newAuthority;
        emit AuthorityUpdated(_newAuthority);
    }

    function setAuthority(IAuthority _newAuthority) external {
        _onlyGovernor();
        authority = _newAuthority;
        emit AuthorityUpdated(_newAuthority);
    }

    /* ========== INTERNAL CHECKS ========== */

    function _onlyGovernor() internal view {
        if (msg.sender != authority.governor()) revert UNAUTHORIZED();
    }

    function _onlyGuardian() internal view {
        if (msg.sender != authority.guardian()) revert UNAUTHORIZED();
    }

    function _onlyPolicy() internal view {
        if (msg.sender != authority.policy()) revert UNAUTHORIZED();
    }

    function _onlyVault() internal view {
        if (msg.sender != authority.vault()) revert UNAUTHORIZED();
    }
}
